#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\LKJ\Desktop\Teach2Give\projects\javascript\Assignment3\node_modules\.pnpm\json-server@1.0.0-beta.2\node_modules\json-server\lib\node_modules;C:\Users\LKJ\Desktop\Teach2Give\projects\javascript\Assignment3\node_modules\.pnpm\json-server@1.0.0-beta.2\node_modules\json-server\node_modules;C:\Users\LKJ\Desktop\Teach2Give\projects\javascript\Assignment3\node_modules\.pnpm\json-server@1.0.0-beta.2\node_modules;C:\Users\LKJ\Desktop\Teach2Give\projects\javascript\Assignment3\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/LKJ/Desktop/Teach2Give/projects/javascript/Assignment3/node_modules/.pnpm/json-server@1.0.0-beta.2/node_modules/json-server/lib/node_modules:/mnt/c/Users/LKJ/Desktop/Teach2Give/projects/javascript/Assignment3/node_modules/.pnpm/json-server@1.0.0-beta.2/node_modules/json-server/node_modules:/mnt/c/Users/LKJ/Desktop/Teach2Give/projects/javascript/Assignment3/node_modules/.pnpm/json-server@1.0.0-beta.2/node_modules:/mnt/c/Users/LKJ/Desktop/Teach2Give/projects/javascript/Assignment3/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../json-server/lib/bin.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../json-server/lib/bin.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../json-server/lib/bin.js" $args
  } else {
    & "node$exe"  "$basedir/../json-server/lib/bin.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
